"use strict";

const { createMemoryCodePoints } = require('./memory-code-points');
const codePointsDataBrowser = require('./code-points-data-browser');
const saslprepModule = require('./index');

const codePoints = createMemoryCodePoints(codePointsDataBrowser.default);
const saslprep = saslprepModule.default.bind(null, codePoints);

// Assign the saslprep function to the module exports object
// and also add it as a named property for convenience.
Object.assign(exports, {
  saslprep,
  default: saslprep
});
