// Import the 'encoding' module here so that it can be easily mocked or replaced for testing.
const encoding = require("./lib/encoding");

// Export a single object with the TextEncoder and TextDecoder properties.
// This makes it easier to use the module by allowing users to destructure
// only the parts they need.
module.exports = {
  TextEncoder: encoding.TextEncoder,
  TextDecoder: encoding.TextDecoder,
};

// Add some type definitions to help TypeScript users.
/** @type {import("./lib/encoding").TextEncoder} */
module.exports.TextEncoder;

/** @type {import("./lib/encoding").TextDecoder} */
module.exports.TextDecoder;

