// rewritten and improved code

// This is a custom error class that extends the built-in ReferenceError class
class CustomReferenceError extends ReferenceError {
  constructor(message: string) {
    super(message);
    // Maintains proper stack trace for where our error was thrown (only available in V8)
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, CustomReferenceError);
    }
  }
}

// Export the custom error class
export = CustomReferenceError;

