// No need to declare const TypeError. It is already defined in the global scope.

// Instead of exporting TypeError constructor directly, create a custom error class that inherits from TypeError
class CustomTypeError extends TypeError {
    constructor(message: string) {
        super(message); // call the constructor of the parent class with the message as argument
        this.name = this.constructor.name; // set the name property of the instance to the name of the constructor
    }
}

// export the custom error class
export = CustomTypeError;

